INSERT INTO `ey_archives` VALUES ('174', '10', '1', '0', '六项涉钛金属国家标准将于五月起实施', '', '0', '0', '0', '1', '0', 'admin', '234', '0', '', '0', '', '', '2020年5月1日起，将有四项涉钛产品国家标准和两项涉钛金属成分分析国家标准开始实施，钛行业相关企业应做好对接准备，学习参照实施。具体情况说明如下：一、四项涉及海绵钛、钛钢复合板等钛产品的国家标准为：1、海绵钛国家标准GB/T 2524-2019旧版', '0.00', '0', '', '1', '100', 'cn', '1', '0', '0', '1587889037', '1587889135');
INSERT INTO `ey_archives` VALUES ('175', '12', '1', '0', '钛板表面热处理和表面冷塑性形变的实验方法', '', '0', '0', '0', '0', '0', 'admin', '191', '0', '', '0', '', '', '烧结温度，温度过高会使碳化钛晶粒长大速度加快。碳化钛高锰钢结硬质合金的最终烧结温度一般取1420℃较合适。钛板厂家认为烧结温度不宜过高。甚至使粘结相变成液相金属流失，从而使硬质相发生邻接、聚集并长大，形成碎裂源。这就是前面分析的硬质相晶粒之间的粘结相', '0.00', '0', '', '1', '100', 'cn', '1', '0', '0', '1587889319', '1587889319');
